import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { toast } from 'sonner'

export const useUser = () => {
    const { register, handleSubmit, reset, watch, setError, formState: { errors } } = useForm();
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const checkEmailExists = (email, accounts) => {
        return accounts.some(account => account.email === email);
    };

    const signUpUser = async (data) => {
        console.log(data);
        // Check if passwords match
        if (data.password !== data.confirmPassword) {
            setError('confirmPassword', { message: 'Passwords do not match' });
            return;
        }

        let accounts = JSON.parse(localStorage.getItem('accounts')) || [];

        if (checkEmailExists(data.email, accounts)) {
            setErrorMessage('Email already registered.');
            return;
        }

        accounts.push(data);
        localStorage.setItem('accounts', JSON.stringify(accounts));

        toast.success('Account successfully registered!');
        reset();
        setErrorMessage('');
    };


    const handleSignIn = async (e, data) => {
        e.preventDefault();
        try {
          await login(data);
          // Redirect to the dashboard after successful login
          navigate('/dashboard');
        } catch (error) {
          console.error('Login failed:', error);
        }
      };
    const handleSignOut = () => {
        setIsLoggedIn(false);
    };
    return {
        
        isLoggedIn,
        errorMessage,
        signUpUser: handleSubmit(signUpUser), 
        handleSignIn: handleSubmit(handleSignIn),
        handleSignOut,
        register,
        watch,
        errors,
    };
};
