import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory from react-router-dom
import styled from 'styled-components';

const BillingOperations = () => {
  const Main = styled('div')`
    font-family: sans-serif;
    background: #f0f0f0;
    height: 100vh;
  `;

  const DropDownContainer = styled('div')`
    width: 15em;
    margin: 0 auto;
    color: #be800d;
  `;

  const DropDownHeader = styled('div')`
    margin-bottom: 0.8em;
    padding: 0.4em 2em 0.4em 1em;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
    font-weight: 500;
    font-size: 1.3rem;
    color: #3faffa;
  `;

  const DropDownListContainer = styled('div')`
    position: absolute;
    z-index: 100;
    width: 15em;
    color: #be800d;
  `;

  const DropDownList = styled('ul')`
    padding: 0;
    margin: 0;
    padding-left: 1em;
    background: #ffffff;
    border: 2px solid #e5e5e5;
    box-sizing: border-box;
    color: #3faffa;
    font-size: 1.3rem;
    font-weight: 500;
    &:first-child {
      padding-top: 0.8em;
    }
  `;

  const ListItem = styled('li')`
    list-style: none;
    margin-bottom: 0.8em;
    &:hover {
      color: #fd9e46;
    }
  `;

  const Button = styled('button')`
    background-color: #007bff;
    color: #fff;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  `;

  const options = ['Mangoes', 'Apples', 'Oranges'];

  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const history = useHistory();

  const toggling = () => setIsOpen(!isOpen);

  const onOptionClicked = value => () => {
    setSelectedOption(value);
    setIsOpen(false);
    console.log(selectedOption);
  };

  const navigateToCorrespondingPage = () => {
    // Redirect to the corresponding page based on the selected option
    if (selectedOption === 'Mangoes') {
      history.push('/mango-page'); // Adjust the route as needed
    } else if (selectedOption === 'Apples') {
      history.push('/apple-page'); // Adjust the route as needed
    } else if (selectedOption === 'Oranges') {
      history.push('/orange-page'); // Adjust the route as needed
    }
  };

  return (
    <Main>
      <DropDownContainer>
        <DropDownHeader onClick={toggling}>
          {selectedOption || 'Choose your Billers'}
        </DropDownHeader>
        {isOpen && (
          <DropDownListContainer>
            <DropDownList>
              {options.map(option => (
                <ListItem onClick={onOptionClicked(option)} key={Math.random()}>
                  {option}
                </ListItem>
              ))}
            </DropDownList>
          </DropDownListContainer>
        )}
      </DropDownContainer>
      <Button onClick={navigateToCorrespondingPage}>Go to Corresponding Page</Button>
    </Main>
  );
};

export default BillingOperations;
