import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const BillingOperations = () => {
  const history = useNavigate();
  const [selectedOption, setSelectedOption] = useState(null);
  // State to manage the selected option

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    // Perform the desired action based on the selected option
    // For now, let's navigate to a new page
    history.push(`/result/${option}`);
  };

  return (
    <div>
      <label>Choose your method:</label>
      <select
        value={selectedOption}
        onChange={(e) => handleOptionSelect(e.target.value)}
      >
        <option value="" disabled>
          Choose an option
        </option>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>

      <p>Selected option: {selectedOption}</p>
    </div>
  );
};


export default BillingOperations;
